import React from "react";
import {TextField} from "@material-ui/core";
import "../../style/tableStyle.css"

const RowEditGarage = ({handleEditClick, handleSaveEditClick,element, renderTableHeader}) =>{

    return(
        <tr>
            {
                renderTableHeader(element).map((column,index)=>{
                    return (
                        <td key={index}>
                            <input key={index}
                            type={"text"}
                            name={element[`${column}`]}
                            value={element[`${column}`]}
                            onChange={(event)=>element[`${column}`] = event.target.value}
                            required
                            /></td>
                        );
                })
            }
            <td>
                <button type="button" onClick={event=> handleSaveEditClick()}>
                    save
                </button>
                <button type="button" onClick= {(event => handleEditClick(event,null))}>
                    cancel
                </button>
            </td>
        </tr>
    );

};

export default RowEditGarage;
