import React from 'react';

class GarageDetails extends React.Component{
    
}

const mapper = function(garage) {

    return (
        <>
             <div className="row g-5">
                            <div className="col-md-4 ">
                                {garage.image ? <img alt="Lien est incorrect" className= "rounded" src={garage.image} ></img> : <div className="noImage"></div> }
                                <input className="col-md-12 form-control" value={garage.image} onChange ={(event)=>this.ImageInputChange(event)} placeholder="Ajouter le lien d'une image"></input>
                            </div>
                            

                            <div className="col-md-8">
                                <div className="row g-5">
                                    <div className="col-md-12">
                                        <label  className="form-label">Nom: </label>
                                        <input type="text" className="form-control" aria-describedby="emailHelp" value={garage.nom} onChange ={(event)=>this.nameInputChange(event)}></input>
                                        {/* <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div> */}
                                    </div>

                                    <div className="col-md-12">
                                        <label  className="form-label">Numéro de téléphone: </label>
                                        <input type="text" className="form-control"  value={garage.adresse} onChange ={(event)=>this.adresseInputChange(event)}></input>
                                    </div>

                                    <div className="col-md-12">
                                        <label  className="form-label">Address: </label>
                                        <input type="text" className="form-control" value={garage.numtel} onChange ={(event)=>this.phoneNumberInputChange(event)}></input>
                                    </div>
                                </div>
                            </div>
                            
                        </div>
        </>
    );
};

export default mapper;
