import React from 'react';
import { deleteAsync } from "../API/API";

const mapper = function(repair) {

    return (
        <>
            <td className="col-md-1"><img src={repair.lienimage} alt="Illustration de la rÃ©paration" className="rounded col-md-12"></img></td>
            <td className="col-md-2">{repair.libelle}</td>
            <td className="col-md-4">{repair.description}</td>
            <td className="col-md-1">{repair.toLocaleDateString()}</td>
            <td className="col-md-2">
                <button type="button" className="btn btn-primary" onClick={() => window.location.pathname = "Mechanic/Edit/"+repair.id}>Modifier</button>
                <button type="button" className="btn btn-danger"  onClick={async()=>  await deleteAsync("mecano/", repair.id)}>Supprimer</button>
            </td>
        </>
    );
};

export default mapper;
