import React, {useState} from "react";
import "../style/connectionStyle.css";
import {login} from "./API";
import {
    Button,
    TextField,
    Grid,
    Paper,
    Typography
} from "@material-ui/core";


class Connection extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password:'',
            loaded:false,
            loading:false,
        errorMassage:false,
        connected:false};

    }
    dissmisError(){
        this.setState({error:""});
    }
    async handleSubmit(){
        this.setState({error:false,
        errorMessage:"",
        loading:true,
        loaded:false});

        try{
            const data = await login(this.state.email,this.state.password);
            if(data.user.userType !== "mecano")
                throw new Error("Un client ne peut pas se connecter à l'application!");
            this.props.login(data);
            this.setState({connected:true});
        }catch (error) {
            this.setState({
                error : true,
                loading:false,
                loaded:true,
                errorMessage:error
            });
        }
    }
    userChange(evt){
        this.setState({email:evt.target.value});
    }
    passwordChange(evt){
        this.setState({password:evt.target.value});
    }


    render() {
        return(
            <div className="formConnection">
                <h1>Avant tout veuillez vous connecter</h1>
                <form>
                    <label>email</label>
                    <input type="text" name="id" />

                    <label>password</label>
                    <input type="password" name="password"/>

                    <input type="submit" value="se connecter" />
                </form>
            </div>

        );
    }
}
export default Connection;
