import React from 'react';
import './App.css';
import Routes from "./routes/Routes";
import SideBar from "./component/Nav/SideBar"
import Login from "./component/Login/Connection";

class App extends React.Component{

  constructor(props){
    super(props);
    this.state = {user : undefined}
  }

  componentDidUpdate() {
    if(this.state.user === undefined){
        this.setState({user:this.props.userStore});
    }
}

   IsConnected (){
    let isConnected = this.state.user.userType === "mecano";
    if(isConnected){
      return <SideBar/>
    }
    else{
      return <Login/>
    }
  }
  
  render() {
    return (
      <div className="App" >
        {IsConnected()}
          <Routes/>
      </div>
    );
  }
  
} 

export default App;
