import React from "react";
import "../../style/GarageDetails.css";
import { getByIdAsync, postAsync, updateAsync } from "../API/API";
import { withRouter} from 'react-router-dom';



class MechanicDetails extends React.Component{
     
    constructor(props) {
        super(props);
        this.state = {
            mechanic: {},
            model: this.props.model,
            isEditMode: this.props.isEditMode,
            
        }
    }

    async componentDidMount() {
        if(this.state.isEditMode){
            const id  = this.props.match.params.id;
            await getByIdAsync(this.state.model.apiRoute, id).then(response=>this.setState({mechanic:response})).catch(error=>console.error(error));
        }
    }


    

    imageLinkChange(evt){
        this.setState({imageLink:evt.target.value});
    }

    async handlesubmit(event){
        event.preventDefault();
        if(this.state.isEditMode){
            await updateAsync(this.state.model.apiRoute, this.state.mechanic).then(() => 
            this.props.history.push("/"+this.state.model.route)
            );
        }else{
            await postAsync(this.state.model.apiRoute, this.state.mechanic.nom,  this.state.mechanic.nom,  this.state.mechanic.adresse,  this.state.mechanic.numtel).then(() => 
            this.props.history.push("/"+this.state.model.route)
            );
        }
    }

    changeImage(event){
        event.preventDefault();
        this.setState({...this.state.mechanic, image: this.state.imageLink});
    }

    onChange(event){
        let elemToSave = this.state.mechanic;
        elemToSave[event.target.name]  = event.target.value;
        this.setState({[event.target.name] : event.target.value})
    }

    render() {
        return (
            <div className="GarageDetails">
                   <form className="">
                    <h1>{this.state.isEditMode ? "Modification" : "Ajout"} d'{this.state.model.determinative} {this.state.model.label}</h1>
                    
                    <div className="col-md-6">
                        <div className="row g-2">
                            <label  className="form-label">Nom: </label>
                            <input type="text" className="form-control" aria-describedby="emailHelp" value={this.state.mechanic.nom} onChange ={(event)=>this.nameInputChange(event)}></input>
                            
                            <label  className="form-label">Prénom: </label>
                            <input type="text" className="form-control" aria-describedby="emailHelp" value={this.state.mechanic.nom} onChange ={(event)=>this.nameInputChange(event)}></input>
                    </div>
                            

                            <div className="col-md-8">
                                <div className="row g-5">
                                    <div className="col-md-12">
                                        <label  className="form-label">Nom: </label>
                                        <input type="text" className="form-control" aria-describedby="emailHelp" value={this.state.mechanic.nom} onChange ={(event)=>this.nameInputChange(event)}></input>
                                        {/* <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div> */}
                                    </div>

                                    <div className="col-md-12">
                                        <label  className="form-label">Numéro de téléphone: </label>
                                        <input type="text" className="form-control"  value={this.state.mechanic.adresse} onChange ={(event)=>this.adresseInputChange(event)}></input>
                                    </div>

                                    <div className="col-md-12">
                                        <label  className="form-label">Address: </label>
                                        <input type="text" className="form-control" value={this.state.mechanic.numtel} onChange ={(event)=>this.phoneNumberInputChange(event)}></input>
                                    </div>
                                </div>
                            </div>
                            
                        </div>
                        
                        <button type="submit" className="btn btn-primary col-md-12 submit" onClick={(event) => this.handlesubmit(event)}>{this.state.isEditMode  ? "Modifier": "Ajouter"}</button>

                   </form>
            </div>
        )
    }
}

export default withRouter(MechanicDetails);
