import React from "react";
import "../../style/GarageDetails.css";
import { getGarageById } from "../API/API";


class GarageDetails extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            garage: {},
            isEditMode: true,
            
        }
    }

    async componentDidMount() {
        await getGarageById(1).then(response=>this.setState({garage:response})).catch(error=>console.error(error));
    }

    changeImage(event){
        event.preventDefault();
        this.setState({...this.state.garage, image: this.state.imageLink});
        console.log(this.state.garage);
    }

    imageLinkChange(evt){
        this.setState({imageLink:evt.target.value});
    }

    handlesubmit(){
        if(this.state.isEditMode){

        }else{

        }
    }

    ImageInputChange(evt){
        let elemToSave = this.state.element;
        elemToSave.image = evt.target.value;
        console.log(elemToSave);
        this.setState({garage:elemToSave});
    }
    nameInputChange(evt){
        let elemToSave = this.state.element;
        elemToSave.nom = evt.target.value;
        this.setState({garage:elemToSave});
    }
    adresseInputChange(evt){
        let elemToSave = this.state.element;
        elemToSave.adresse = evt.target.value;
        this.setState({garage:elemToSave});
    }
    phoneNumberInputChange(evt){
        let elemToSave = this.state.element;
        elemToSave.numtel = evt.target.value;
        this.setState({garage:elemToSave});
    }

    render() {
        return (
            <div className="GarageDetails">
                   <form className="">
                        <h1>{this.state.isEditMode ? "Modification" : "Ajout"} d'un garage</h1>

                        <div className="row g-4">
                            <div className="col-md-4 ">
                                <img alt="Illustration du garage" className={this.state.isEditMode  ? "rounded":"placeholder rounded"} src={this.state.garage.image} ></img>
                                <input className="col-md-8" value={this.state.garage.image} onChange ={(event)=>this.ImageInputChange(event)}></input>
                                <button type="submit" className="btn btn-primary col-md-4" onClick={(event)=>this.changeImage(event)}>Changer l'image</button>
                            </div>
                            

                            <div className="col-md-8">
                                <div className="row g-4">
                                    <div className="col-md-12">
                                        <label  className="form-label">Nom: </label>
                                        <input type="email" className="form-control" aria-describedby="emailHelp" value={this.state.garage.nom} onChange ={(event)=>this.nameInputChange(event)}></input>
                                        {/* <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div> */}
                                    </div>

                                    <div className="col-md-12">
                                        <label  className="form-label">Numéro de téléphone: </label>
                                        <input type="email" className="form-control"  value={this.state.garage.adresse} onChange ={(event)=>this.adresseInputChange(event)}></input>
                                    </div>

                                    <div className="col-md-12">
                                        <label  className="form-label">Address: </label>
                                        <input type="email" className="form-control" value={this.state.garage.numtel} onChange ={(event)=>this.phoneNumberInputChange(event)}></input>
                                    </div>
                                </div>
                            </div>
                            
                        </div>
                        
                        <button type="submit" className="btn btn-primary col-md-12 submit" onClick={this.handlesubmit()}>{this.state.isEditMode  ? "Modifier": "Ajouter"}</button>

                   </form>
            </div>
        )
    }
}

export default GarageDetails;
