import React from "react";
import {TextField} from "@material-ui/core";
import "../../style/tableStyle.css"

class RowEditGarage extends React.Component{
    constructor(props) {
        super(props);
        this.garage = this.props.garage;
        this.handleEditClick = this.props.handleEditClick;
    }
    render() {
        return (
            <tr>
                <td>
                    <input
                        type={"text"}
                        name={"Nom"}
                        placeholder={"Nom"}
                        value={garage.nom}
                        required
                    />
                </td>
                <td>
                    <input
                        type={"text"}
                        name={"adresse"}
                        placeholder={"Adresse"}
                        value={garage.adresse}
                        required
                    />
                </td>
                <td>
                    <input
                        type={"text"}
                        name={"Numéro de téléphone"}
                        placeholder={"Numéro de téléphone"}
                        value={garage.numtel}
                        required
                    />
                </td>
                <td>
                    <input
                        type={"text"}
                        name={"image"}
                        placeholder={"Url de l'image"}
                        value={garage.image}
                        required
                    />
                </td>
                <td>
                    <button type="button">
                        save
                    </button>
                    <button type="button" onClick={(event => handleEditClick(event, null))}>
                        cancel
                    </button>
                </td>
            </tr>
        );
    }
}

export default RowEditGarage;
