import React, {Fragment} from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Redirect,
} from "react-router-dom";

import SideBar from "../component/Nav/SideBar"
import Connection from "../component/Login/Connection"
import GarageList from "../component/Garage/GarageList"
import GarageDetails from "../component/Garage/GarageDetails"
import {connect} from "react-redux";
import Modeles from "../Models/Models";



class Routes extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
            userStore : undefined,
    };
}
    isConnected(){
        return localStorage.getItem("Token");
    }
    

    render() {
        const HomeComponent = () => this.isConnected()? <SideBar/> : <Redirect to={"/Login"}/>
        const LoginComponent = () => this.isConnected() ? <Redirect to={"/"}/> : <Connection/>
       
        return (
            <div >
                <Router>
                    <Switch>
                        <Route exact path="/Login" component={LoginComponent}/>
                        <Route exact path="/" component={HomeComponent}/>
                        
                        <Route exact path="/Garage" render={() => 
                            this.isConnected() ?
                            <Fragment>
                            <SideBar />
                            <GarageList model={Modeles.garage} />
                            </Fragment>
                            :
                             <Redirect to="/login"/>
                        } />
                        <Route exact path="/Garage/Edit/:id" render={() =>
                            this.isConnected() ? 
                            <Fragment>
                            <SideBar />
                            <GarageDetails isEditMode={true} model={Modeles.garage}/>
                            </Fragment>
                            :
                             <Redirect to="/login"/>
                        } />
                        <Route exact path="/Garage/Add" render={() =>
                            this.isConnected() ? 
                            <Fragment>
                            <SideBar />
                            <GarageDetails isEditMode={false} model={Modeles.garage}/>
                            </Fragment>
                            :
                             <Redirect to="/login"/>
                        } />

                        <Route exact path="/Mechanic" render={() => 
                            this.isConnected() ?
                            <Fragment>
                            <SideBar />
                            <GarageList model={Modeles.mechanic} />
                            </Fragment>
                            :
                             <Redirect to="/login"/>
                        } />

                    </Switch>
                </Router>
            </div>
        );
}
}
const mapStateToProps = (state) => {
    return {
        userStore: state.login.userStore,
    }
};


export default connect(mapStateToProps)(Routes);
