import React from "react";
import "../../style/GarageDetails.css";
import { getByIdAsync, postAsync, updateAsync } from "../API/API";
import { withRouter, Redirect} from 'react-router-dom';



class GarageDetails extends React.Component{
     
    constructor(props) {
        super(props);
        this.state = {
            element: this.props.model.object,
            model: this.props.model,
            isEditMode: this.props.isEditMode, 
        }
    }

    async componentDidMount() {
        if(this.state.isEditMode){
            const id  = this.props.match.params.id;
            await getByIdAsync(this.state.model.apiRoute, id).then(response=>this.setState({element:response})).catch(error=>console.error(error));
        }
    }


    

    imageLinkChange(evt){
        this.setState({imageLink:evt.target.value});
    }

    async handlesubmit(event){
        event.preventDefault();
        if(this.state.isEditMode){
            await updateAsync(this.state.model.apiRoute, this.state.element).then(() => 
            this.props.history.push("/"+this.state.model.route)
            );
        }else{
            await postAsync(this.state.model.apiRoute, this.state.element.nom,  this.state.element.nom,  this.state.element.adresse,  this.state.element.numtel).then(() => 
            this.props.history.push("/"+this.state.model.route)
            );
        }
    }

    changeImage(event){
        event.preventDefault();
        this.setState({...this.state.element, image: this.state.imageLink});
    }

    ImageInputChange(evt){
        let elemToSave = this.state.element;
        elemToSave.image = evt.target.value;
        this.setState({element:elemToSave});
    }
    nameInputChange(evt){
        let elemToSave = this.state.element;
        elemToSave.nom = evt.target.value;
        this.setState({element:elemToSave});
    }
    adresseInputChange(evt){
        let elemToSave = this.state.element;
        elemToSave.adresse = evt.target.value;
        this.setState({element:elemToSave});
    }
    phoneNumberInputChange(evt){
        let elemToSave = this.state.element;
        elemToSave.numtel = evt.target.value;
        this.setState({element:elemToSave});
    }

    render() {
        return (
            <div className="GarageDetails">
                   <form className="">
                    <h1>{this.state.isEditMode ? "Modification" : "Ajout"} d'{this.state.model.determinative} {this.state.model.label}</h1>

                        {this.state.model.formBody(this.state.element)}
                        
                        <button type="submit" className="btn btn-primary col-md-12 submit" onClick={(event) => this.handlesubmit(event)}>{this.state.isEditMode  ? "Modifier": "Ajouter"}</button>

                   </form>
            </div>
        )
    }
}

export default withRouter(GarageDetails);
