import React from "react";
import "./connectionStyle.css";
import {login} from "../API";



class Connection extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password:'',
            loaded:false,
            loading:false,
        errorMassage:false,
        connected:false};
    }
    
    async handleSubmit(){
        try{
            const data = await login(this.state.email,this.state.password);
            if(data.user.userRecup.userType !== "mecano") {
                throw new Error("Un client ne peut pas se connecter à l'application!");
            }
            this.props.login(data.user.userRecup);
            console.log(this.props.user.userType);
            this.setState({
                connected:true});
        }catch (error) {
            this.setState({
                error : true,
                loading:false,
                loaded:true,
                errorMessage:error
            });
        }
    }
    userChange(evt){
        this.setState({email:evt.target.value});
    }
    passwordChange(evt){
        this.setState({password:evt.target.value});
    }


    render() {
            // // if(this.state.connected){
            // //     return <Redirect to={"/menu"}/>
            // }
            // // let contentMessage;
            // if(!this.state.loaded)
            //     contentMessage = null;
            // else if(this.state.loading){
            //     contentMessage = <Grid item><Typography component={"h6"} color={"error"}>Chargement en cours</Typography> </Grid>;
            // }else if(this.state.error){
            //     contentMessage = <Grid item><Typography component={"h6"} color={"error"}>{this.state.errorMassage}</Typography> </Grid>
            // }
            return (
                <div className="form">
                    <h1>Se connecter</h1>
                    <form className="row g-3">
                    <input
                     className="form-control"
                     type="text"
                     id="emailAdresse"
                     placeholder="Adresse mail"
                     value = {this.state.email}
                     onChange ={(event)=>this.userChange(event)}
                     autoFocus
                     required
                     ></input>

                    <input
                    className="form-control"
                    type="text"
                    id="password"
                    placeholder="Password"
                    value = {this.state.password}
                    onChange ={(event)=>this.passwordChange(event)}
                    required
                    ></input>
                    <button 
                    type="submit" 
                    className="btn btn-primary"
                    onClick ={()=>{
                        this.handleSubmit();
                    }}
                    >Connexion</button>
                    </form>
                </div>
            )


    }
}
const mapDispatchToProps = (dispatch)=>{
    return {
        login : (user)=>{
            dispatch({type:"login", payload:{userInfo:user}})
        }
    }
}
export default Connection(undefined,mapDispatchToProps)(Connection);
