import React from "react";
import { getAllGarage } from "../API/API";
import "../../style/GarageList.css";

class GarageList extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            elements: [],
        }
        console.log("this.state.elements");
    }

    componentDidMount() {
        getAllGarage().then(response=>this.setState({elements:response})).catch(error=>console.error(error));
        console.log(this.state.elements);
    }

    render() {
        return (
            <div className="GarageList">
                 <table>
                    <thead>
                        <tr>
                            {
                                this.renderTableHeader(this.state.elements[0]).map((title,index)=>{
                                        return <th key={index}>{title}</th>;

                                })
                            }
                            <th key={"titleTh"}>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                    {
                        this.state.elements.map((element,index)=>(
                            <Fragment key={index}>
                                { this.state.idEdit === index ? (
                                    <RowEditGarage handleEditClick = {this.handleEditClick} handleSaveEditClick={this.handleSaveEditClick} element={element} renderTableHeader={this.renderTableHeader}/>
                                ) : (
                                    <RowReadGarage element={element} handleEditClick={this.handleEditClick} handleDeleteClick = {this.deleteElement} index={index} renderTableHeader={this.renderTableHeader}/>
                                )}
                            </Fragment>
                            ))}
                    </tbody>
                </table>
            </div>
        )
    }
}
export default GarageList;
