import React from "react";
import "../../style/GarageDetails.css";
import { getGarageById } from "../API/API";


class GarageDetails extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            garage: {},
            isEditMode: false,
            imageLink:"https://upload.wikimedia.org/wikipedia/commons/8/85/Ciney_JPG02.jpg"
        }
    }

    async componentDidMount() {
        await getGarageById(1).then(response=>this.setState({garage:response})).catch(error=>console.error(error));
    }

    changeImage(event){
        event.preventDefault();
        this.setState({...this.state.garage, image: this.state.imageLink});
        console.log(this.state.garage);
    }

    imageLinkChange(evt){
        this.setState({imageLink:evt.target.value});
    }

    render() {
        return (
            <div className="GarageDetails">
                   <form className="">
                        <h1>{this.state.isEditMode ? "Modification" : "Ajout"} d'un garage</h1>

                        <div className="row g-4">
                            <div className="col-md-4 ">
                                <img alt="Illustration du garage" className={this.state.isEditMode  ? "rounded":"placeholder rounded"} src={this.state.image} ></img>
                                <input className="col-md-8" value={this.state.imageLink} onChange ={(event)=>this.imageLinkChange(event)}></input>
                                <button type="submit" className="btn btn-primary col-md-4" onClick={(event)=>this.changeImage(event)}>Changer l'image</button>
                            </div>
                            

                            <div className="col-md-8">
                                <div className="row g-4">
                                    <div className="col-md-12">
                                        <label for="exampleInputEmail1" className="form-label">Nom: </label>
                                        <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" value={this.state.garage.nom}></input>
                                        {/* <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div> */}
                                    </div>

                                    <div className="col-md-12">
                                        <label for="exampleInputEmail1" className="form-label">Numéro de téléphone: </label>
                                        <input type="email" className="form-control" id="exampleInputEmail1" value={this.state.garage.adresse}></input>
                                    </div>

                                    <div className="col-md-12">
                                        <label for="exampleInputEmail1" className="form-label">Address: </label>
                                        <input type="email" className="form-control" id="exampleInputEmail1" value={this.state.garage.numtel}></input>
                                    </div>
                                </div>
                            </div>
                            
                        </div>
                        
                        <button type="submit" className="btn btn-primary col-md-12 submit">{this.state.isEditMode  ? "Modifier": "Ajouter"}</button>

                   </form>
            </div>
        )
    }
}

export default GarageDetails;
